import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import axios from 'axios';
import './login.css';

const styles = {//Стили для страницы
    position: 'absolute',
    top: '70px',
    left: '50%',
    transform: 'translateX(-50%)',
}

export default ({
    changeLoggedIn
